function getTinyMCELang(lang){
	var defaultLang = "en"	;	
	if (tinyMCE_languages.inArray(lang)) defaultLang=lang;
	return defaultLang;
}
Array.prototype.inArray = function (value) {
	var i;
	for (i=0; i < this.length; i++) {
		if (this[i] === value) return true;
	}
	return false;
};

