(function(){tinymce.PluginManager.requireLangPack('definitionlist');tinymce.create('tinymce.plugins.DefinitionListPlugin',{init:function(ed,url){ed.addCommand('mceDefinitionList',function(){var se=ed.selection;var term="<dt>"+ed.translate('definitionlist.term')+"</dt>\n<dd>"+ed.translate('definitionlist.definition')+"</dd>\n";var p=ed.dom.getParent(se.getNode(),'DL');if(se.isCollapsed()&&!p){ed.windowManager.confirm(ed.translate('definitionlist.instructions'),function(s){if(s){var html='<dl class="exe-dl">\n';html+=term;html+="</dl><br />";tinyMCE.execCommand('mceInsertContent',false,html)}})}else{var n=ed.dom.getNext(se.getNode(),'DT');if(n)$(n).before($(term));else $(p).append($(term));p=ed.dom.getParent(se.getNode(),'DL');var DTs=p.getElementsByTagName("DT");for(i=0;i<DTs.length;i++){if(DTs[i].innerHTML==ed.translate('definitionlist.term')){ed.selection.setCursorLocation(DTs[i],1)}}}});ed.onInit.add(function(){if(ed.settings.content_css!==false)ed.dom.loadCSS(url+"/css/content.css")});function getFriendlyURL(t){t=t.replace(/[^\w\s]/gi,'');t=t.replace(/\ /g,"-");t=t.toLowerCase();return t}ed.onSaveContent.add(function(ed,o){if($("DT",o.content).length==0)return false;var c=o.content;var w=$('<div id="definitionlist-parser" style="display:none"></div>');$("BODY").append(w);w.html(c);$("DT",w).each(function(){var p=$(this).parent();if(p.length==1&&p.attr("class")=="exe-dl"){var t=$(this).text();var id=getFriendlyURL(t);if(id.length==0)$(this).removeAttr("id");else $(this).attr("id",id)}});o.content=w.html();w.remove()});ed.addButton('definitionlist',{title:'definitionlist.title',cmd:'mceDefinitionList',image:url+'/img/definitionlist.gif'})},getInfo:function(){return{longname:'Definitions lists',author:'Ignacio Gros',authorurl:'http://www.gros.es',version:"1.0"}}});tinymce.PluginManager.add('definitionlist',tinymce.plugins.DefinitionListPlugin)})();