(function(){tinymce.PluginManager.requireLangPack('pastecode');tinymce.create('tinymce.plugins.PasteCodePlugin',{init:function(ed,url){ed.addCommand('mcePasteCode',function(){ed.windowManager.open({file:url+'/pastecode.htm',width:500,height:400,inline:1},{plugin_url:url})});ed.addCommand('mcePasteHtml',function(){ed.windowManager.open({file:url+'/pastehtml.htm',width:500,height:400,inline:1},{plugin_url:url})});ed.onInit.add(function(){if(ed.settings.content_css !== false)ed.dom.loadCSS(url+"/css/content.css");});ed.addButton('pastehtml',{title:'pastecode.paste_html_desc',cmd:'mcePasteHtml',image:url+'/img/pastehtml.gif'});ed.addButton('pastecode',{title:'pastecode.paste_code_desc',cmd:'mcePasteCode',image:url+'/img/pastecode.gif'})},getInfo:function(){return{longname:'Paste code',author:'Ignacio Gros',authorurl:'http://www.gros.es',version:"2.1"}}});tinymce.PluginManager.add('pastecode',tinymce.plugins.PasteCodePlugin)})();