(function(){tinymce.PluginManager.requireLangPack('tooltip');tinymce.create('tinymce.plugins.TooltipPlugin',{init:function(ed,url){ed.addCommand('mceTooltip',function(){var se=ed.selection;if(se.isCollapsed()&&!ed.dom.getParent(se.getNode(),'A')){ed.windowManager.alert('tooltip.selection_error');return}ed.windowManager.open({file:url+'/tooltip.htm',width:500,height:400,inline:1},{plugin_url:url})});ed.onInit.add(function(){if(ed.settings.content_css!==false)ed.dom.loadCSS(url+"/css/content.css")});ed.addButton('tooltip',{title:'tooltip.title',cmd:'mceTooltip',image:url+'/img/tooltip.gif'})},getInfo:function(){return{longname:'Tooltips',author:'Ignacio Gros',authorurl:'http://www.gros.es',version:"2.0"}}});tinymce.PluginManager.add('tooltip',tinymce.plugins.TooltipPlugin)})();